import { useContext, useEffect, useState } from "react";
import { SearchContext } from "../../context/SearchContext";
import useFetch from "../../hooks/useFetch";
import { AuthContext } from "../../context/AuthContext";
import "./reserve.css";

const Reserve = ({ setOpen, hotelId }) => {
  const { dates } = useContext(SearchContext);
  const { user } = useContext(AuthContext);
  const [selectedRooms, setSelectedRooms] = useState([]);

  const checkInDate = dates[0].startDate;
  const checkOutDate = dates[0].endDate;

  const { data: availableRooms, loading, error } = useFetch(
    `/rooms/available?hotelId=${hotelId}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`
  );

  const handleSelectRoom = (roomId) => {
    setSelectedRooms((prev) => {
      if (prev.includes(roomId)) {
        return prev.filter((id) => id !== roomId);
      } else {
        return [...prev, roomId];
      }
    });
  };

  const handleBooking = async () => {
    if (user && user.token && selectedRooms.length > 0) {
      const bookingData = {
        userId: user._id,
        hotelId,
        roomId: selectedRooms,
        checkInDate,
        checkOutDate,
      };
  
      try {
        const response = await fetch("http://localhost:8800/api/bookings", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${user.token}`, // Gửi token trong header
          },
          body: JSON.stringify(bookingData),
        });
  
        const result = await response.json();
        if (response.ok) {
          alert("Đặt phòng thành công!");
          setOpen(false);
        } else {
          alert("Đặt phòng thất bại: " + result.message);
        }
      } catch (error) {
        console.error("Error booking room:", error);
        alert("Đã xảy ra lỗi khi đặt phòng.");
      }
    } else {
      alert("Vui lòng chọn ít nhất một phòng và đảm bảo bạn đã đăng nhập.");
    }
  };
  
  

  return (
    <div className="reserve">
      <button onClick={() => setOpen(false)}>Đóng</button>
      <h2>Các phòng trống</h2>
      {loading ? (
        <p>Đang tải...</p>
      ) : error ? (
        <p>Đã xảy ra lỗi: {error.message}</p>
      ) : availableRooms.length > 0 ? (
        <ul>
          {availableRooms.map((room) => (
            <li key={room._id}>
              <input
                type="checkbox"
                id={room._id}
                onChange={() => handleSelectRoom(room._id)}
              />
              <label htmlFor={room._id}>
                <h3>{room.title}</h3>
                <p>{room.desc}</p>
                <span>Giá: ${room.price}</span>
              </label>
            </li>
          ))}
        </ul>
      ) : (
        <p>Không có phòng trống trong khoảng thời gian đã chọn.</p>
      )}
      <h3>Phòng đã chọn: {selectedRooms.length > 0 ? selectedRooms.join(", ") : "Không có phòng nào được chọn"}</h3>
      <button onClick={handleBooking} disabled={selectedRooms.length === 0}>Đặt phòng</button>
    </div>
  );
};

export default Reserve;
