import React, { useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import useFetch from "../../hooks/useFetch";
import "./booking.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // Import CSS for toast notifications

const Booking = () => {
    const { user } = useContext(AuthContext); // Lấy thông tin user từ context
    const { data, loading, error, reFetch } = useFetch(`http://localhost:8800/api/bookings/history`); // Gọi API mà không cần userId ở đây

    useEffect(() => {
        if (user) {
            reFetch(); // Gọi lại API khi user đã sẵn sàng
        }
    }, [user, reFetch]);

    if (loading) {
        return <div>Loading...</div>; // Hiển thị khi dữ liệu đang tải
    }

    if (error) {
        return <div>Something went wrong: {error.message}</div>; // Hiển thị nếu có lỗi
    }

    // Lọc dữ liệu booking theo userId
    const userBookings = data.filter((booking) => booking.userId === user._id); // Lọc theo ID của user

    return (
        <div className="userBookingHistory">
            <h2>Your Booking History</h2>
            {userBookings.length > 0 ? (
                <table className="bookingTable">
                    <thead>
                        <tr>
                            <th>Hotel Name</th>
                            <th>Room Title</th>
                            <th>Check-in Date</th>
                            <th>Check-out Date</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {userBookings.map((booking) => (
                            <tr key={booking._id}>
                                <td>{booking.hotelId.name}</td> {/* Lấy thông tin khách sạn */}
                                <td>{booking.roomId.title}</td> {/* Lấy tiêu đề phòng */}
                                <td>{new Date(booking.checkInDate).toLocaleDateString()}</td>
                                <td>{new Date(booking.checkOutDate).toLocaleDateString()}</td>
                                <td>{booking.status}</td> {/* Trạng thái của đặt phòng */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No bookings found</p> // Hiển thị nếu không có đặt phòng nào
            )}
            <ToastContainer />
        </div>
    );
};

export default Booking;
