import React, { useContext } from "react";
import { AuthContext } from "../../../context/AuthContext";
import './hostBookings.css'; // Import file CSS cho HostBookings
import useFetch from "../../../hooks/useFetch"; 
import SideBar from "../../../components/sidebar/SideBar"; // Đảm bảo đường dẫn đúng

const HostBookings = () => {
  const { user } = useContext(AuthContext);

  // Gọi API để lấy danh sách đặt phòng của host
  const { data, loading, error } = useFetch(user ? `http://localhost:8800/api/bookings/host?hostId=${user._id}` : null);

  // Kiểm tra nếu user không tồn tại
  if (!user) {
    return <p className="no-bookings">Vui lòng đăng nhập để xem danh sách đặt phòng.</p>;
  }

  if (loading) return <p className="loader">Đang tải...</p>;
  if (error) return <p className="no-bookings">Đã xảy ra lỗi: {error.message}</p>;

  return (
    <div className="host-bookings-page">
      <SideBar />
      <div className="host-bookings-container">
        <h2>Danh sách đặt phòng của bạn</h2>
        {data.length > 0 ? (
          <div className="bookings-grid">
            {data.map((booking) => (
              <div className="booking-item" key={booking._id}>
                <h3>Khách sạn: {booking.hotelId.name}</h3>
                <p>Phòng: {booking.roomId.title}</p>
                <p>Người đặt: {booking.userId.username}</p>
                <p>Ngày nhận phòng: {new Date(booking.checkInDate).toLocaleDateString()}</p>
                <p>Ngày trả phòng: {new Date(booking.checkOutDate).toLocaleDateString()}</p>
                <p className={`status ${booking.status}`}>Trạng thái: {booking.status}</p>
                <button className="delete-button">Xóa</button>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-bookings">Không có đặt phòng nào.</p>
        )}
      </div>
    </div>
  );
};

export default HostBookings;
