import React, { useEffect } from "react";
import { useContext, useState } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";

const List = () => {
  const { user } = useContext(AuthContext); // Lấy thông tin người dùng từ AuthContext
  const [hotels, setHotels] = useState([]); // State để lưu danh sách khách sạn
  const [loading, setLoading] = useState(true); // State để kiểm tra trạng thái loading
  const [error, setError] = useState(null); // State để lưu thông báo lỗi

  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const res = await axios.get(`/api/hotels/host/${user._id}`); // Gọi API để lấy danh sách khách sạn
        setHotels(res.data); // Lưu danh sách khách sạn vào state
      } catch (err) {
        setError(err); // Nếu có lỗi, lưu lỗi vào state
      } finally {
        setLoading(false); // Đặt trạng thái loading là false khi hoàn thành
      }
    };

    if (user) {
      fetchHotels(); // Gọi hàm fetchHotels nếu người dùng đã đăng nhập
    }
  }, [user]);

  if (loading) return <div>Loading...</div>; // Hiển thị loading khi đang lấy dữ liệu
  if (error) return <div>Failed to load hotels: {error.message}</div>; // Hiển thị thông báo lỗi nếu có

  return (
    <div>
      <h2>Danh sách khách sạn của bạn</h2>
      {hotels.length === 0 ? (
        <p>Không có khách sạn nào được thêm vào.</p>
      ) : (
        <ul>
          {hotels.map((hotel) => (
            <li key={hotel._id}>
              <h3>{hotel.name}</h3>
              <p>{hotel.desc}</p>
              <p>Địa chỉ: {hotel.address}</p>
              <p>Giá thấp nhất: ${hotel.cheapestPrice}</p>
              {/* Thêm các thông tin khác mà bạn muốn hiển thị */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default List
