import React, { useContext, useState } from "react";
import { AuthContext } from "../../../context/AuthContext";
import { toast, ToastContainer } from "react-toastify";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "react-toastify/dist/ReactToastify.css";
import "./newHotel.css";

const NewHotel = () => {
  const { user } = useContext(AuthContext);
  const [name, setName] = useState("");
  const [type, setType] = useState("");
  const [city, setCity] = useState("");
  const [address, setAddress] = useState("");
  const [distance, setDistance] = useState("");
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [cheapestPrice, setCheapestPrice] = useState("");
  const [images, setImages] = useState([]);
  const navigate = useNavigate();

  if (!user || user.role !== "host") {
    toast.error("Bạn không có quyền truy cập trang này.");
    return null; // Không hiển thị gì nếu không phải là host
  }

  const handleImageChange = (e) => {
    const files = e.target.files;
    const imagesArray = Array.from(files);
    setImages(imagesArray);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Upload images to Cloudinary
    const imageUrls = [];
    for (const image of images) {
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", "upload");

      try {
        const res = await axios.post(
          "https://api.cloudinary.com/v1_1/djqfhed9n/image/upload",
          formData
        );
        imageUrls.push(res.data.secure_url);
      } catch (err) {
        toast.error("Đã xảy ra lỗi khi upload hình ảnh.");
        return;
      }
    }

    try {
      await axios.post("http://localhost:8800/api/hotels", {
        name,
        type,
        city,
        address,
        distance,
        title,
        desc,
        cheapestPrice,
        hostId: user._id,
        images: imageUrls,
      });
      toast.success("Tạo khách sạn thành công!");
      navigate("/listHotel");
      setName("");
      setType("");
      setCity("");
      setAddress("");
      setDistance("");
      setTitle("");
      setDesc("");
      setCheapestPrice("");
      setImages([]);
    } catch (err) {
      toast.error("Đã xảy ra lỗi khi tạo khách sạn.");
    }
  };

  return (
    <div className="newHotel">
      <form onSubmit={handleSubmit} className="hotelForm">
        <h2>Tạo Khách Sạn Mới</h2>
        <input
          type="text"
          placeholder="Tên Khách Sạn"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Loại Khách Sạn"
          value={type}
          onChange={(e) => setType(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Thành Phố"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Địa Chỉ"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Khoảng Cách"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Tiêu Đề"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Mô Tả"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Giá Thấp Nhất"
          value={cheapestPrice}
          onChange={(e) => setCheapestPrice(e.target.value)}
          required
        />
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageChange}
        />
        <button type="submit">Tạo Khách Sạn</button>
      </form>

      <div className="image-preview">
        {images.length > 0 && (
          <h3>Hình ảnh đã chọn:</h3>
        )}
        {images.map((image, index) => (
          <img
            key={index}
            src={URL.createObjectURL(image)}
            alt={`preview-${index}`}
            className="preview-image"
          />
        ))}
      </div>

      <ToastContainer />
    </div>
  );
};

export default NewHotel;
