import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const NewRoom = () => {
  const { hotelId } = useParams(); // Lấy hotelId từ URL
  const [hotels, setHotels] = useState([]); // Danh sách khách sạn
  const [selectedHotelId, setSelectedHotelId] = useState(hotelId); // ID khách sạn đã chọn
  const [roomData, setRoomData] = useState({
    title: '', // Tiêu đề
    price: '', // Giá
    maxPeople: '', // Số người tối đa
    desc: '', // Mô tả
  });

  // Hàm lấy danh sách khách sạn từ API
  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const response = await axios.get('/api/hotels'); // Cập nhật đường dẫn API nếu cần
        setHotels(response.data);
      } catch (error) {
        toast.error('Lỗi khi tải danh sách khách sạn: ' + error.response.data.message);
        console.error('Lỗi:', error.response.data);
      }
    };

    fetchHotels();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRoomData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Gửi yêu cầu POST đến API với selectedHotelId và roomData
      const response = await axios.post(`/api/rooms/${selectedHotelId}`, roomData);
      toast.success('Tạo phòng thành công!');
      console.log('Phòng đã được tạo:', response.data);
      // Reset form hoặc chuyển hướng nếu cần
      setRoomData({ title: '', price: '', maxPeople: '', desc: '' }); // Reset form
      setSelectedHotelId(''); // Reset hotel ID
    } catch (error) {
      toast.error('Tạo phòng thất bại: ' + error.response.data.message);
      console.error('Lỗi khi tạo phòng:', error.response.data);
    }
  };

  return (
    <div className="new-room">
      <h1>Tạo Phòng Mới</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Khách Sạn</label>
          <select
            value={selectedHotelId}
            onChange={(e) => setSelectedHotelId(e.target.value)}
            required
          >
            <option value="">Chọn khách sạn</option>
            {hotels.map((hotel) => (
              <option key={hotel._id} value={hotel._id}>
                {hotel.name} {/* Hiển thị tên khách sạn */}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Tiêu Đề</label>
          <input
            type="text"
            name="title"
            value={roomData.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Giá</label>
          <input
            type="number"
            name="price"
            value={roomData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Số Người Tối Đa</label>
          <input
            type="number"
            name="maxPeople"
            value={roomData.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mô Tả</label>
          <textarea
            name="desc"
            value={roomData.desc}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Tạo Phòng</button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default NewRoom;
