import { useContext, useState, useEffect } from "react";
import { SearchContext } from "../../context/SearchContext";
import { AuthContext } from "../../context/AuthContext";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // Import CSS for toast notifications
import "./reserve.css";

const Reserve = ({ setOpen, hotelId }) => {
  const { dates } = useContext(SearchContext);
  const { user } = useContext(AuthContext);
  const [selectedRooms, setSelectedRooms] = useState([]);
  const [availableRooms, setAvailableRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const checkInDate = dates[0].startDate;
  const checkOutDate = dates[0].endDate;

  // Hàm để lấy các phòng còn trống
  const fetchAvailableRooms = async () => {
    try {
      const response = await fetch(
        `/rooms/available?hotelId=${hotelId}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`
      );
      if (!response.ok) {
        throw new Error("Không thể lấy danh sách phòng.");
      }
      const data = await response.json();
      setAvailableRooms(data);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAvailableRooms(); // Gọi hàm để lấy danh sách phòng còn trống khi component mount
  }, [hotelId, checkInDate, checkOutDate]);

  const handleSelectRoom = (roomId) => {
    setSelectedRooms((prev) => {
      if (prev.includes(roomId)) {
        return prev.filter((id) => id !== roomId);
      } else {
        return [...prev, roomId];
      }
    });
  };

  const handleBooking = async () => {
    if (selectedRooms.length > 0) {
      const bookingData = {
        userId: user ? user._id : null,
        hotelId,
        roomId: selectedRooms,
        checkInDate,
        checkOutDate,
      };

      try {
        const response = await fetch("http://localhost:8800/api/bookings", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(bookingData),
        });

        const result = await response.json();
        if (response.ok) {
          toast.success("Đặt phòng thành công!"); // Thông báo thành công
          setOpen(false);
        } else {
          toast.error("Đặt phòng thất bại: " + result.message); // Thông báo thất bại
        }
      } catch (error) {
        console.error("Error booking room:", error);
        toast.error("Đã xảy ra lỗi khi đặt phòng."); // Thông báo lỗi
      }
    } else {
      toast.warning("Vui lòng chọn ít nhất một phòng."); // Thông báo nếu không chọn phòng
    }
  };

  return (
    <div className="reserve">
      <div className="rContainer">
        <span className="rClose" onClick={() => setOpen(false)}>✖</span>
        <h2 className="rTitle">Các phòng trống</h2>
        {loading ? (
          <p>Đang tải...</p>
        ) : error ? (
          <p>Đã xảy ra lỗi: {error.message}</p>
        ) : availableRooms.length > 0 ? (
          availableRooms.map((room) => (
            <div className="rItem" key={room._id}>
              <div className="room">
                <h3 className="rTitle">{room.title}</h3>
                <p className="rDesc">{room.desc}</p>
                <span className="rPrice">Giá: ${room.price}</span>
                <label>
                  <input
                    type="checkbox"
                    id={room._id}
                    onChange={() => handleSelectRoom(room._id)}
                  />
                  Chọn phòng
                </label>
              </div>
            </div>
          ))
        ) : (
          <p>Đã hết phòng.</p>
        )}
        <button className="rButton" onClick={handleBooking} disabled={selectedRooms.length === 0}>
          Đặt phòng
        </button>
      </div>
      <ToastContainer />
    </div>
  );
};

export default Reserve;
