import React, { useContext, useEffect, useState } from 'react';
import useFetch from '../../hooks/useFetch';
import { SearchContext } from '../../context/SearchContext';
import { AuthContext } from '../../context/AuthContext'; // Import AuthContext
import axios from 'axios';
import './reserve.css';

const Reserve = ({ setOpen, hotelId }) => {
  const { dates, options } = useContext(SearchContext);
  const { user } = useContext(AuthContext); // Lấy thông tin người dùng từ AuthContext
  const [availableRooms, setAvailableRooms] = useState([]);
  const [selectedRooms, setSelectedRooms] = useState([]);

  const MILLISECONDS_PER_DAY = 1000 * 60 * 60 * 24;

  const dayDifference = (date1, date2) => {
    const timeDiff = Math.abs(date2.getTime() - date1.getTime());
    return Math.ceil(timeDiff / MILLISECONDS_PER_DAY);
  };

  const totalDays = dayDifference(dates[0].endDate, dates[0].startDate);

  // Lấy thông tin phòng còn trống từ server
  const { data, loading, error } = useFetch(`/rooms/available?hotelId=${hotelId}&startDate=${dates[0].startDate}&endDate=${dates[0].endDate}`);

  useEffect(() => {
    if (data) {
      setAvailableRooms(data);
    }
  }, [data]);

  const handleSelect = (roomId) => {
    setSelectedRooms((prev) => {
      if (prev.includes(roomId)) {
        return prev.filter((id) => id !== roomId);
      } else {
        return [...prev, roomId];
      }
    });
  };

  const handleReserve = async () => {
    try {
      await axios.post('/reservations', {
        roomIds: selectedRooms,
        hotelId,
        userId: user._id, // Sử dụng ID người dùng từ context
        dates,
      });
      // Xử lý sau khi đặt phòng thành công
      setOpen(false);
    } catch (err) {
      console.error(err);
      // Xử lý lỗi ở đây
    }
  };

  return (
    <div className="reserve">
      <h2>Chọn phòng</h2>
      {loading ? (
        "Loading..."
      ) : error ? (
        <span>{error.message}</span>
      ) : (
        availableRooms.map((room) => (
          <div key={room._id} className="room">
            <input
              type="checkbox"
              id={room._id}
              onChange={() => handleSelect(room._id)}
            />
            <label htmlFor={room._id}>
              {room.name} - Giá: ${room.price} cho {totalDays} đêm
            </label>
          </div>
        ))
      )}
      <button onClick={handleReserve} disabled={selectedRooms.length === 0}>
        Đặt phòng
      </button>
    </div>
  );
};

export default Reserve;
