import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../context/AuthContext'; 
import { ToastContainer, toast } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css'; 
import './newRoom.css'; 
import Navbar from "../../../components/sidebar/SideBar";

const NewRoom = () => {
  const [room, setRoom] = useState({
    title: '',
    price: '',
    maxPeople: '',
    desc: '',
    roomNumbers: [],
  });

  const { user } = useContext(AuthContext); 
  const [hotelId, setHotelId] = useState(''); 
  const [hotels, setHotels] = useState([]); // State để lưu danh sách khách sạn

  // Lấy danh sách khách sạn của host khi component được mount
  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const response = await axios.get(`http://localhost:8800/api/hotels`, {
          headers: {
            Authorization: `Bearer ${user.token}`, 
          },
        });
        setHotels(response.data); // Lưu danh sách khách sạn vào state
      } catch (err) {
        console.error("Failed to fetch hotels:", err);
        toast.error('Không thể lấy danh sách khách sạn!');
      }
    };
    
    fetchHotels();
  }, [user.token]); // Chỉ gọi lại khi token thay đổi

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'roomNumbers') {
      const numbers = value.split(',').map(num => ({ number: num.trim() }));
      setRoom({ ...room, roomNumbers: numbers });
    } else {
      setRoom({ ...room, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:8800/api/rooms/${hotelId}`, room, {
        headers: {
          Authorization: `Bearer ${user.token}`, 
        },
      });
      toast.success('Phòng đã được tạo thành công!'); 
    } catch (err) {
      console.error("Failed to create room:", err);
      toast.error('Tạo phòng thất bại!'); 
    }
  };

  return (
    <div className="new-room-container">
      <Navbar/>
      <ToastContainer />
      <h1 className="title">Tạo Phòng Mới</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Tên Phòng</label>
          <input
            type="text"
            name="title"
            value={room.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Giá</label>
          <input
            type="number"
            name="price"
            value={room.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Số Người Tối Đa</label>
          <input
            type="number"
            name="maxPeople"
            value={room.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mô Tả</label>
          <textarea
            name="desc"
            value={room.desc}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label>Số Phòng</label>
          <input
            type="text"
            name="roomNumbers"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Chọn Khách Sạn</label>
          <select 
            value={hotelId} 
            onChange={(e) => setHotelId(e.target.value)} 
            required
          >
            <option value="">Chọn khách sạn...</option>
            {hotels.map((hotel) => (
              <option key={hotel._id} value={hotel._id}>
                {hotel.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="submit-button">Tạo Phòng</button>
      </form>
    </div>
  );
};

export default NewRoom;
