import React, { useContext } from "react";
import { AuthContext } from "../../../context/AuthContext";
import './hostBookings.css'; // Import file CSS cho HostBookings
import useFetch from "../../../hooks/useFetch"; 
import SideBar from "../../../components/sidebar/SideBar"; // Đảm bảo đường dẫn đúng
import { ToastContainer, toast } from "react-toastify"; // Import ToastContainer và toast
import 'react-toastify/dist/ReactToastify.css'; // Import CSS cho Toastify

const HostBookings = () => {
  const { user } = useContext(AuthContext);

  // Gọi API để lấy tất cả danh sách đặt phòng
  const { data, loading, error } = useFetch("http://localhost:8800/api/bookings");

  // Kiểm tra nếu user không tồn tại
  if (!user) {
    return <p className="no-bookings">Vui lòng đăng nhập để xem danh sách đặt phòng.</p>;
  }

  if (loading) return <p className="loader">Đang tải...</p>;
  if (error) return <p className="no-bookings">Đã xảy ra lỗi: {error.message}</p>;

  // Hàm để xử lý xóa đặt phòng
  const handleDeleteBooking = async (bookingId) => {
    try {
      const response = await fetch(`http://localhost:8800/api/bookings/${bookingId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success("Đã xóa đặt phòng thành công!"); // Hiển thị thông báo thành công
        setTimeout(() => {
          window.location.reload(); // Reload lại trang
        }, 1000); // Thời gian delay trước khi reload
      } else {
        throw new Error("Xóa đặt phòng thất bại.");
      }
    } catch (error) {
      toast.error(error.message); // Hiển thị thông báo lỗi
    }
  };

  return (
    <div className="host-bookings-page">
      <SideBar />
      <div className="host-bookings-container">
        <h2>Danh sách đặt phòng của bạn</h2>
        {data.length > 0 ? (
          <table className="bookings-table">
            <thead>
              <tr>
                <th>Khách sạn</th>
                <th>Phòng</th>
                <th>Người đặt</th>
                <th>Ngày nhận phòng</th>
                <th>Ngày trả phòng</th>
                <th>Trạng thái</th>
                <th>Thao tác</th>
              </tr>
            </thead>
            <tbody>
              {data.map((booking) => (
                <tr key={booking._id}>
                  <td>{booking.hotelId.name}</td> {/* Hiển thị tên khách sạn */}
                  <td>{booking.roomId.title}</td>
                  <td>{booking.userId.username}</td>
                  <td>{new Date(booking.checkInDate).toLocaleDateString()}</td>
                  <td>{new Date(booking.checkOutDate).toLocaleDateString()}</td>
                  <td className={`status ${booking.status}`}>{booking.status}</td>
                  <td>
                    <button className="delete-button" onClick={() => handleDeleteBooking(booking._id)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="no-bookings">Không có đặt phòng nào.</p>
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default HostBookings;
