import "./newRoom.css";
import { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import useFetch from "../../../hooks/useFetch";

const NewRoom = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const hotelId = location.pathname.split("/")[3]; // Lấy ID khách sạn từ URL
  const [room, setRoom] = useState({
    title: "",
    price: "",
    maxPeople: "",
    desc: "",
  });
  const [hotelOptions, setHotelOptions] = useState([]); // Danh sách khách sạn
  const [selectedHotelId, setSelectedHotelId] = useState(""); // ID khách sạn đã chọn

  const { data: hotels, loading, error } = useFetch("/hotels?hostId=YOUR_HOST_ID"); // Thay YOUR_HOST_ID bằng ID của host

  useEffect(() => {
    if (hotels) {
      setHotelOptions(hotels);
    }
  }, [hotels]);

  const handleChange = (e) => {
    setRoom({ ...room, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newRoom = { ...room, hotelId: selectedHotelId };
    // Gửi request tạo phòng mới
    console.log(newRoom);
    // Thực hiện API call để tạo phòng ở đây
  };

  return (
    <div className="newRoom">
      <h1 className="addRoomTitle">Thêm Phòng Mới</h1>
      <form className="addRoomForm" onSubmit={handleSubmit}>
        <div className="addRoomItem">
          <label>Tên Phòng</label>
          <input
            name="title"
            type="text"
            placeholder="Tên Phòng"
            onChange={handleChange}
          />
        </div>
        <div className="addRoomItem">
          <label>Giá</label>
          <input
            name="price"
            type="number"
            placeholder="Giá Phòng"
            onChange={handleChange}
          />
        </div>
        <div className="addRoomItem">
          <label>Số Người Tối Đa</label>
          <input
            name="maxPeople"
            type="number"
            placeholder="Số Người Tối Đa"
            onChange={handleChange}
          />
        </div>
        <div className="addRoomItem">
          <label>Mô Tả</label>
          <input
            name="desc"
            type="text"
            placeholder="Mô Tả"
            onChange={handleChange}
          />
        </div>
        <div className="addRoomItem">
          <label>Khách Sạn</label>
          <select
            onChange={(e) => setSelectedHotelId(e.target.value)}
            value={selectedHotelId}
          >
            <option value="">Chọn Khách Sạn</option>
            {hotelOptions.map((hotel) => (
              <option key={hotel._id} value={hotel._id}>
                {hotel.name}
              </option>
            ))}
          </select>
        </div>
        <button className="addRoomButton" type="submit">Thêm</button>
      </form>
    </div>
  );
};

export default NewRoom;
