import React, { useEffect, useContext, useState } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import SideBar from "../../../components/sidebar/SideBar";
import { ToastContainer, toast } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css'; 
import { useNavigate } from "react-router-dom"; 
import "./newRoom.css";

const NewRoom = () => {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [room, setRoom] = useState({
    title: '',
    price: '',
    maxPeople: '',
    desc: '',
    roomNumbers: [],
  });
  const { user } = useContext(AuthContext);
  const navigate = useNavigate(); // Khởi tạo useNavigate
  const [hotelId, setHotelId] = useState(''); // State lưu ID khách sạn

  useEffect(() => {
    // Kiểm tra nếu người dùng không phải là host
    if (!user || user.role !== "host") {
      toast.error("Bạn không có quyền truy cập vào trang này."); // Hiển thị thông báo lỗi
      navigate("/"); // Chuyển hướng về trang chủ
      return; // Dừng thực thi tiếp theo
    }

    const fetchHotels = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/hotels/host/${user._id}`);
        setHotels(res.data);
      } catch (err) {
        console.error("Failed to load hotels:", err);
        toast.error('Không thể tải danh sách khách sạn!'); // Hiển thị thông báo lỗi
      } finally {
        setLoading(false);
      }
    };

    fetchHotels();
  }, [user, navigate]); // Thêm navigate vào dependencies

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'roomNumbers') {
      const numbers = value.split(',').map(num => ({ number: num.trim(), unavailableDates: [] })); // Thêm trường unavailableDates cho từng phòng
      setRoom({ ...room, roomNumbers: numbers });
    } else {
      setRoom({ ...room, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!hotelId) {
      toast.error("Vui lòng chọn khách sạn trước khi tạo phòng.");
      return; // Dừng nếu không chọn khách sạn
    }
    
    try {
      await axios.post(`http://localhost:8800/api/rooms/${hotelId}`, room, {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });
      toast.success('Phòng đã được tạo thành công!');
      navigate("/rooms"); // Chuyển hướng đến danh sách phòng hoặc trang khác sau khi tạo
    } catch (err) {
      console.error("Failed to create room:", err);
      toast.error('Tạo phòng thất bại!');
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>; // Hiển thị loading khi đang tải
  }

  return (
    <div className="new-room-container">
      <ToastContainer />
      <h1 className="title">Tạo Phòng Mới</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Tên Phòng</label>
          <input
            type="text"
            name="title"
            value={room.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Giá</label>
          <input
            type="number"
            name="price"
            value={room.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Số Người Tối Đa</label>
          <input
            type="number"
            name="maxPeople"
            value={room.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mô Tả</label>
          <textarea
            name="desc"
            value={room.desc}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label>Số Phòng (cách nhau bằng dấu phẩy)</label>
          <input
            type="text"
            name="roomNumbers"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Chọn Khách Sạn</label>
          <select
            value={hotelId}
            onChange={(e) => setHotelId(e.target.value)}
            required
          >
            <option value="">Chọn khách sạn</option>
            {hotels.map(hotel => (
              <option key={hotel._id} value={hotel._id}>
                {hotel.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="submit-button">Tạo Phòng</button>
      </form>
    </div>
  );
};

export default NewRoom;
