import React, { useEffect, useContext, useState } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import SideBar from "../../../components/sidebar/SideBar";
import { ToastContainer, toast } from 'react-toastify'; // Nhập ToastContainer và toast
import 'react-toastify/dist/ReactToastify.css'; // Nhập CSS cho toast
import { useNavigate } from "react-router-dom"; // Nhập useNavigate
import "./list.css";

const List = () => {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);
  const navigate = useNavigate(); // Khởi tạo useNavigate

  useEffect(() => {
    // Kiểm tra nếu người dùng không phải là host
    if (!user || user.role !== "host") {
      toast.error("Bạn không có quyền truy cập vào trang này."); // Hiển thị thông báo lỗi
      navigate("/"); // Chuyển hướng về trang chủ
      return; // Dừng thực thi tiếp theo
    }

    const fetchHotels = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/hotels/host/${user._id}`);
        setHotels(res.data);
      } catch (err) {
        console.error("Failed to load hotels:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchHotels();
  }, [user, navigate]); // Thêm navigate vào dependencies

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8800/api/hotels/${id}`);
      setHotels(hotels.filter((hotel) => hotel._id !== id));
      toast.success('Khách sạn đã được xóa thành công!'); // Thông báo thành công
    } catch (err) {
      console.error("Failed to delete hotel:", err);
      toast.error('Xóa khách sạn thất bại!'); // Thông báo thất bại
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="list-container">
      <ToastContainer /> {/* Thêm ToastContainer để hiển thị toast */}
      <SideBar />
      <h1 className="title">Danh sách khách sạn của bạn</h1>
      <table className="hotel-table">
        <thead>
          <tr>
            <th>Hình Ảnh</th>
            <th>Tên Khách Sạn</th>
            <th>Giá Thấp Nhất</th>
            <th>Hành Động</th>
          </tr>
        </thead>
        <tbody>
          {hotels.length > 0 ? (
            hotels.map((hotel) => (
              <tr key={hotel._id}>
                <td><img src={hotel.photos[0]} alt={hotel.name} className="hotel-image" /></td>
                <td>{hotel.name}</td>
                <td>${hotel.cheapestPrice}</td>
                <td>
                  <button className="update-button">Cập nhật</button>
                  <button className="delete-button" onClick={() => handleDelete(hotel._id)}>Xóa</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">Không có khách sạn nào được thêm vào.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default List;
