import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"; 
import { faCircleXmark } from "@fortawesome/free-solid-svg-icons";
import "./reserve.css";
import useFetch from "../../hooks/useFetch";
import { useContext, useState } from "react";
import { SearchContext } from "../../context/SearchContext";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify"; 
import "react-toastify/dist/ReactToastify.css";

const Reserve = ({ setOpen, hotelId }) => {
  const [selectedRooms, setSelectedRooms] = useState([]);
  const { data, loading, error } = useFetch(`/hotels/room/${hotelId}`);
  
  console.log("Data:", data); // Log data
  console.log("Loading:", loading); // Log loading
  console.log("Error:", error); // Log error

  const { dates } = useContext(SearchContext);

  const getDatesInRange = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const date = new Date(start.getTime());
    const dates = [];

    while (date <= end) {
      dates.push(new Date(date).getTime());
      date.setDate(date.getDate() + 1);
    }

    return dates;
  };

  const alldates = getDatesInRange(dates[0].startDate, dates[0].endDate);

  const isAvailable = (roomNumber) => {
    const isFound = roomNumber.unavailableDates.some((date) =>
      alldates.includes(new Date(date).getTime())
    );
    return !isFound; // Trả về true nếu phòng có sẵn
  };

  const handleSelect = (roomId) => {
    if (selectedRooms.includes(roomId)) {
      setSelectedRooms(selectedRooms.filter((id) => id !== roomId)); // Bỏ chọn phòng
    } else {
      setSelectedRooms([...selectedRooms, roomId]); // Chọn phòng
    }
  };

  const navigate = useNavigate();

  const handleClick = async () => {
    if (selectedRooms.length === 0) {
      toast.error("Vui lòng chọn ít nhất một phòng!", {
        position: toast.POSITION.TOP_CENTER,
      });
      return;
    }

    try {
      await Promise.all(
        selectedRooms.map((roomId) => {
          const res = axios.put(`/rooms/availability/${roomId}`, {
            dates: alldates,
          });
          return res.data;
        })
      );
      setOpen(false);
      toast.success("Phòng đã được đặt thành công!", {
        position: toast.POSITION.TOP_CENTER,
      });
      navigate("/");
    } catch (err) {
      toast.error("Có lỗi xảy ra khi đặt phòng!", {
        position: toast.POSITION.TOP_CENTER,
      });
    }
  };

  // Nếu có lỗi, hiển thị thông báo lỗi
  if (error) {
    return <div className="error">Có lỗi xảy ra: {error.message}</div>;
  }

  return (
    <div className="reserve">
      <div className="rContainer">
        <FontAwesomeIcon
          icon={faCircleXmark}
          className="rClose"
          onClick={() => setOpen(false)}
        />
        <span>Select your rooms:</span>
        {loading ? (
          "Loading..."
        ) : (
          data && data.map((item) => ( // Đảm bảo dữ liệu được trả về
            <div className="rItem" key={item._id}>
              <div className="rItemInfo">
                <div className="rTitle">{item.title}</div>
                <div className="rDesc">{item.desc}</div>
                <div className="rMax">
                  Max people: <b>{item.maxPeople}</b>
                </div>
                <div className="rPrice">{item.price}</div>
              </div>
              <div className="rSelectRooms">
                {item.roomNumbers.map((roomNumber) => (
                  <div className="room" key={roomNumber._id}>
                    <span>{roomNumber.number}</span>
                    <button
                      onClick={() => handleSelect(roomNumber._id)}
                      className={`selectButton ${selectedRooms.includes(roomNumber._id) ? "active" : ""}`}
                      disabled={!isAvailable(roomNumber)}
                    >
                      {selectedRooms.includes(roomNumber._id) ? "Selected" : "Select"}
                    </button>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
        <button onClick={handleClick} className="rButton" disabled={selectedRooms.length === 0}>
          Reserve Now!
        </button>
      </div>
      <ToastContainer /> 
    </div>
  );
};

export default Reserve;
