import React, { useContext, useState } from "react"; 
import { AuthContext } from "../../context/AuthContext";
import useFetch from "../../hooks/useFetch";
import axios from "axios"; // Dùng axios để gọi API
import Navbar from "../../components/navbar/Navbar";
import MailList from "../../components/mailList/MailList";
import Footer from "../../components/footer/Footer";
import { ToastContainer, toast } from "react-toastify"; // Import ToastContainer và toast
import 'react-toastify/dist/ReactToastify.css'; // Import CSS cho Toastify
import './bookingHistory.css'; // Import file CSS ở đây

const BookingHistory = () => {
  const { user } = useContext(AuthContext);
  const { data, loading, error, reFetch } = useFetch(user ? `http://localhost:8800/api/bookings/history?userId=${user._id}` : null);
  const [canceling, setCanceling] = useState(false); // Trạng thái hủy đặt phòng

  // Hàm hủy đặt phòng
  const handleCancelBooking = async (bookingId) => {
    setCanceling(true);
    try {
      await axios.put(`http://localhost:8800/api/bookings/cancel/${bookingId}`);
      toast.success("Đã hủy đặt phòng thành công!"); // Hiển thị thông báo thành công
      reFetch(); // Sau khi hủy, lấy lại dữ liệu cập nhật
    } catch (err) {
      console.error("Có lỗi khi hủy đặt phòng:", err);
      toast.error("Có lỗi xảy ra khi hủy đặt phòng."); // Hiển thị thông báo lỗi
    } finally {
      setCanceling(false);
    }
  };

  if (!user) {
    return <p className="no-bookings">Vui lòng đăng nhập để xem lịch sử đặt phòng.</p>;
  }

  if (loading) return <p className="loader">Đang tải...</p>;
  if (error) return <p className="no-bookings">Đã xảy ra lỗi: {error.message}</p>;

  return (
    <div>
      <Navbar />
      <div className="booking-container">
        <h2>Lịch sử đặt phòng</h2>
        {data.length > 0 ? (
          data.map((booking) => (
            <div className="booking-item" key={booking._id}>
              <h3>Khách sạn: {booking.hotelId.name}</h3>
              <p>Phòng: {booking.roomId.title}</p>
              <p>Ngày nhận phòng: {new Date(booking.checkInDate).toLocaleDateString()}</p>
              <p>Ngày trả phòng: {new Date(booking.checkOutDate).toLocaleDateString()}</p>
              <p className={`booking-status ${booking.status}`}>Trạng thái: {booking.status}</p>

              {/* Chỉ hiển thị nút hủy nếu trạng thái là confirmed */}
              {booking.status === 'confirmed' && (
                <button
                  className="cancel-button"
                  onClick={() => handleCancelBooking(booking._id)}
                  disabled={canceling}
                >
                  {canceling ? "Đang hủy..." : "Hủy đặt phòng"}
                </button>
              )}
            </div>
          ))
        ) : (
          <p className="no-bookings">Không có đặt phòng nào.</p>
        )}
      </div>
      <MailList />
      <Footer />
      <ToastContainer /> {/* Thêm ToastContainer để hiển thị thông báo */}
    </div>
  );
};

export default BookingHistory;
