import React, { useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../../context/AuthContext'; // Nhập AuthContext để lấy thông tin người dùng
import { ToastContainer, toast } from 'react-toastify'; // Nhập ToastContainer và toast
import 'react-toastify/dist/ReactToastify.css'; // Nhập CSS cho toast
import './newRoom.css'; // Nhập CSS của trang
import Navbar from "../../../components/sidebar/SideBar"

const NewRoom = () => {
  const [room, setRoom] = useState({
    title: '',
    price: '',
    maxPeople: '',
    desc: '',
    roomNumbers: [],
  });
  
  const { user } = useContext(AuthContext); // Lấy thông tin người dùng từ AuthContext
  const [hotelId, setHotelId] = useState(''); // State để lưu hotelId

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'roomNumbers') {
      // Chuyển đổi chuỗi nhập vào thành mảng các số phòng
      const numbers = value.split(',').map(num => ({ number: num.trim() }));
      setRoom({ ...room, roomNumbers: numbers });
    } else {
      setRoom({ ...room, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:8800/api/rooms/${hotelId}`, room, {
        headers: {
          Authorization: `Bearer ${user.token}`, // Đính kèm token xác thực
        },
      });
      toast.success('Phòng đã được tạo thành công!'); // Thông báo thành công
    } catch (err) {
      console.error("Failed to create room:", err);
      toast.error('Tạo phòng thất bại!'); // Thông báo thất bại
    }
  };

  return (
    <div className="new-room-container">
      <ToastContainer />
      <h1 className="title">Tạo Phòng Mới</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Tên Phòng</label>
          <input
            type="text"
            name="title"
            value={room.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Giá</label>
          <input
            type="number"
            name="price"
            value={room.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Số Người Tối Đa</label>
          <input
            type="number"
            name="maxPeople"
            value={room.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mô Tả</label>
          <textarea
            name="desc"
            value={room.desc}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label>Số Phòng</label>
          <input
            type="text"
            name="roomNumbers"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>ID Khách Sạn</label>
          <input
            type="text"
            value={hotelId}
            onChange={(e) => setHotelId(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="submit-button">Tạo Phòng</button>
      </form>
    </div>
  );
};

export default NewRoom;
