import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const NewRoom = () => {
  const { hotelId } = useParams(); // Lấy hotelId từ URL
  const [roomData, setRoomData] = useState({
    title: '', // Tiêu đề
    price: '', // Giá
    maxPeople: '', // Số người tối đa
    desc: '', // Mô tả
    // Thêm các trường khác nếu cần
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRoomData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Gửi yêu cầu POST đến API với hotelId và roomData
      const response = await axios.post(`/api/rooms/${hotelId}`, roomData);
      toast.success('Tạo phòng thành công!');
      console.log('Phòng đã được tạo:', response.data);
      // Reset form hoặc chuyển hướng nếu cần
      setRoomData({ title: '', price: '', maxPeople: '', desc: '' }); // Reset form
    } catch (error) {
      toast.error('Tạo phòng thất bại: ' + error.response.data.message);
      console.error('Lỗi khi tạo phòng:', error.response.data);
    }
  };

  return (
    <div className="new-room">
      <h1>Tạo Phòng Mới</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Tiêu Đề</label>
          <input
            type="text"
            name="title"
            value={roomData.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Giá</label>
          <input
            type="number"
            name="price"
            value={roomData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Số Người Tối Đa</label>
          <input
            type="number"
            name="maxPeople"
            value={roomData.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mô Tả</label>
          <textarea
            name="desc"
            value={roomData.desc}
            onChange={handleChange}
            required
          />
        </div>
        {/* Thêm các trường khác nếu cần */}
        <button type="submit">Tạo Phòng</button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default NewRoom;
