import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const NewRoom = () => {
  const { hotelId } = useParams(); // Lấy hotelId từ URL
  const [roomData, setRoomData] = useState({
    title: '',
    price: '',
    maxPeople: '',
    desc: '',
    // Thêm các trường khác nếu cần
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRoomData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(`/api/rooms/${hotelId}`, roomData);
      toast.success('Room created successfully!');
      console.log('Room created:', response.data);
      // Reset form or redirect if needed
    } catch (error) {
      toast.error('Failed to create room: ' + error.response.data.message);
      console.error('Error creating room:', error.response.data);
    }
  };

  return (
    <div className="new-room">
      <h1>Create New Room</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={roomData.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Price</label>
          <input
            type="number"
            name="price"
            value={roomData.price}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Max People</label>
          <input
            type="number"
            name="maxPeople"
            value={roomData.maxPeople}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Description</label>
          <textarea
            name="desc"
            value={roomData.desc}
            onChange={handleChange}
            required
          />
        </div>
        {/* Thêm các trường khác nếu cần */}
        <button type="submit">Create Room</button>
      </form>
      <ToastContainer />
    </div>
  );
};

export default NewRoom;
