import {
  faBed,
  faCalendarDays,
  faPerson,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./header.css";
import { DateRange } from "react-date-range";
import { useContext, useEffect, useState } from "react";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file
import { format } from "date-fns";
import { useNavigate } from "react-router-dom";
import { SearchContext } from "../../context/SearchContext";
import { AuthContext } from "../../context/AuthContext";
import axios from 'axios'; // Import axios để lấy dữ liệu

const Header = ({ type }) => {
  const [destination, setDestination] = useState(""); // Đặt giá trị mặc định là chuỗi rỗng
  const [openDate, setOpenDate] = useState(false);
  const [dates, setDates] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const [openOptions, setOpenOptions] = useState(false);
  const [options, setOptions] = useState({
    adult: 1,
    children: 0,
    room: 1,
  });

  const [cities, setCities] = useState([]); // State để lưu danh sách thành phố
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  useEffect(() => {
    // Hàm lấy dữ liệu các thành phố từ API
    const fetchCities = async () => {
      try {
        const res = await axios.get('http://localhost:8800/api/hotels/cities'); // Cập nhật đường dẫn API đúng với router của bạn
        console.log(res.data); // Kiểm tra dữ liệu
        setCities(res.data); // Giả sử dữ liệu trả về là một mảng các thành phố
      } catch (err) {
        console.error(err);
      }
    };
    fetchCities();
  }, []);

  const handleOption = (name, operation) => {
    setOptions((prev) => {
      return {
        ...prev,
        [name]: operation === "i" ? options[name] + 1 : options[name] - 1,
      };
    });
  };

  const { dispatch } = useContext(SearchContext);

  const handleSearch = () => {
    console.log({ destination, dates, options }); // Kiểm tra dữ liệu trước khi tìm kiếm
    dispatch({ type: "NEW_SEARCH", payload: { destination, dates, options } });
    navigate("/hotels", { state: { destination, dates, options } });
  };

  return (
    <div className="header">
      <video className="headerVideo" autoPlay loop muted>
        <source src="/assets/video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div
        className={type === "list" ? "headerContainer listMode" : "headerContainer"}
      >
        {type !== "list" && (
          <>
            <h1 className="headerTitle">
              Đặt phòng nhanh chóng, tận hưởng không gian sang trọng!
            </h1>
            <p className="headerDesc">
              Hãy cùng tôi tận hưởng một không gian tiện ích ở mọi nơi - Chỉ với một cú Click
            </p>
            {!user && <button className="headerBtn">Sign in / Register</button>}
            <div className="headerSearch">
              <div className="headerSearchItem">
                <FontAwesomeIcon icon={faBed} className="headerIcon" />
                <select
                  className="headerSearchInput"
                  onChange={(e) => setDestination(e.target.value)}
                  value={destination} // Đặt giá trị mặc định cho ô chọn
                >
                  <option value="">Where are you going?</option>
                  {cities.map((city) => (
                    <option key={city} value={city}>{city}</option> // Hiển thị tên thành phố
                  ))}
                </select>
              </div>
              <div className="headerSearchItem">
                <FontAwesomeIcon icon={faCalendarDays} className="headerIcon" />
                <span
                  onClick={() => setOpenDate(!openDate)}
                  className="headerSearchText"
                >{`${format(dates[0].startDate, "MM/dd/yyyy")} to ${format(
                  dates[0].endDate,
                  "MM/dd/yyyy"
                )}`}</span>
                {openDate && (
                  <DateRange
                    editableDateInputs={true}
                    onChange={(item) => setDates([item.selection])}
                    moveRangeOnFirstSelection={false}
                    ranges={dates}
                    className="date"
                    minDate={new Date()}
                  />
                )}
              </div>
              <div className="headerSearchItem">
                <FontAwesomeIcon icon={faPerson} className="headerIcon" />
                <span
                  onClick={() => setOpenOptions(!openOptions)}
                  className="headerSearchText"
                >{`${options.adult} adult · ${options.children} children · ${options.room} room`}</span>
                {openOptions && (
                  <div className="options">
                    <div className="optionItem">
                      <span className="optionText">Adult</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.adult <= 1}
                          className="optionCounterButton"
                          onClick={() => handleOption("adult", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {options.adult}
                        </span>
                        <button
                          className="optionCounterButton"
                          onClick={() => handleOption("adult", "i")}
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <div className="optionItem">
                      <span className="optionText">Children</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.children <= 0}
                          className="optionCounterButton"
                          onClick={() => handleOption("children", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {options.children}
                        </span>
                        <button
                          className="optionCounterButton"
                          onClick={() => handleOption("children", "i")}
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <div className="optionItem">
                      <span className="optionText">Room</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.room <= 1}
                          className="optionCounterButton"
                          onClick={() => handleOption("room", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {options.room}
                        </span>
                        <button
                          className="optionCounterButton"
                            onClick={() => handleOption("room", "i")}
                          >
                          +
                        </button>
                          </div>
                            </div>
                              </div>
                                )}
                              </div>
                              <div className="headerSearchItem">
                                <button className="headerBtn" onClick={handleSearch}>
                                  Search
                                </button>
                              </div>
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  );
                };
                          
export default Header;
