import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../../context/AuthContext";
import useFetch from "../../hooks/useFetch";
import "./bookingHistory.css";

const BookingHistory = () => {
  const { user } = useContext(AuthContext); // Lấy thông tin user từ context
  const { data, loading, error, reFetch } = useFetch(`/api/bookings/history`); // Gọi API để lấy lịch sử đặt phòng

  useEffect(() => {
    if (user) {
      reFetch(); // Gọi lại API khi user đã sẵn sàng
    }
  }, [user]);

  if (loading) {
    return <div>Loading...</div>; // Hiển thị khi dữ liệu đang tải
  }

  if (error) {
    return <div>Something went wrong: {error.message}</div>; // Hiển thị nếu có lỗi
  }

  return (
    <div className="bookingHistory">
      <h2>Your Booking History</h2>
      {data.length > 0 ? (
        <table className="bookingTable">
          <thead>
            <tr>
              <th>Hotel Name</th>
              <th>Room Number</th>
              <th>Check-in Date</th>
              <th>Check-out Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {data.map((booking) => (
              <tr key={booking._id}>
                <td>{booking.hotelName}</td>
                <td>{booking.roomNumber}</td>
                <td>{new Date(booking.checkInDate).toLocaleDateString()}</td>
                <td>{new Date(booking.checkOutDate).toLocaleDateString()}</td>
                <td>{booking.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No bookings found</p>
      )}
    </div>
  );
};

export default BookingHistory;
