import React, { useEffect, useContext, useState } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import SideBar from "../../../components/sidebar/SideBar";
import "./list.css";

const List = () => {
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext); // Lấy thông tin người dùng từ AuthContext

  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const res = await axios.get(`http://localhost:8800/api/hotels/host/${user._id}`);
        setHotels(res.data); // Lưu danh sách khách sạn vào state
      } catch (err) {
        console.error("Failed to load hotels:", err); // Xử lý lỗi
      } finally {
        setLoading(false); // Kết thúc trạng thái loading
      }
    };

    if (user) {
      fetchHotels(); // Gọi hàm fetchHotels nếu người dùng đã đăng nhập
    }
  }, [user]); // Chạy lại khi user thay đổi

  const handleDelete = async (id) => {
    // Hàm xử lý xóa khách sạn
    try {
      await axios.delete(`http://localhost:8800/api/hotels/${id}`);
      setHotels(hotels.filter((hotel) => hotel._id !== id)); // Cập nhật danh sách sau khi xóa
    } catch (err) {
      console.error("Failed to delete hotel:", err); // Xử lý lỗi
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>; // Hiển thị khi đang loading
  }

  return (
    <div className="list-container">
      <SideBar />
      <h1>Danh sách khách sạn của bạn</h1>
      {hotels.length > 0 ? (
        <div className="hotel-list">
          {hotels.map((hotel) => (
            <div className="hotel-row" key={hotel._id}>
              <img src={hotel.photos[0]} alt={hotel.name} className="hotel-image" />
              <div className="hotel-details">
                <h2>{hotel.name}</h2>
                <p><strong>Giá thấp nhất:</strong> ${hotel.cheapestPrice}</p>
              </div>
              <div className="hotel-actions">
                <button className="update-button">Cập nhật</button>
                <button className="delete-button" onClick={() => handleDelete(hotel._id)}>Xóa</button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>Không có khách sạn nào được thêm vào.</p>
      )}
    </div>
  );
};

export default List;
