import { useContext, useEffect } from "react";
import { SearchContext } from "../../context/SearchContext";
import useFetch from "../../hooks/useFetch";
import "./reserve.css";

const Reserve = ({ setOpen, hotelId }) => {
  const { dates } = useContext(SearchContext);
  
  // Tính toán ngày checkIn và checkOut
  const checkInDate = dates[0].startDate;
  const checkOutDate = dates[0].endDate;

  // Sử dụng useFetch để lấy dữ liệu phòng trống
  const { data: availableRooms, loading, error } = useFetch(
    `/rooms/available?hotelId=${hotelId}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`
  );

  return (
    <div className="reserve">
      <button onClick={() => setOpen(false)}>Đóng</button>
      <h2>Các phòng trống</h2>
      {loading ? (
        <p>Đang tải...</p>
      ) : error ? (
        <p>Đã xảy ra lỗi: {error.message}</p>
      ) : availableRooms.length > 0 ? (
        <ul>
          {availableRooms.map((room) => (
            <li key={room._id}>
              <h3>{room.title}</h3>
              <p>{room.desc}</p>
              <span>Giá: ${room.price}</span>
            </li>
          ))}
        </ul>
      ) : (
        <p>Không có phòng trống trong khoảng thời gian đã chọn.</p>
      )}
    </div>
  );
};

export default Reserve;
