import React, { useEffect, useState, useContext } from 'react';
import { SearchContext } from '../../context/SearchContext'; // Đảm bảo import SearchContext
import axios from 'axios';

const Reserve = () => {
  const { dates, hotelId } = useContext(SearchContext); // Lấy thông tin ngày và hotelId từ context
  const [availableRooms, setAvailableRooms] = useState([]);

  const checkInDate = dates[0].startDate; // Lấy ngày check-in từ dates
  const checkOutDate = dates[0].endDate; // Lấy ngày check-out từ dates

  useEffect(() => {
    const fetchAvailableRooms = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8800/api/rooms/available?hotelId=${hotelId}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`
        );
        setAvailableRooms(res.data); // Cập nhật danh sách phòng còn trống
      } catch (err) {
        console.error(err);
      }
    };
    fetchAvailableRooms();
  }, [hotelId, checkInDate, checkOutDate]);

  return (
    <div>
      <h2>Available Rooms</h2>
      {availableRooms.length === 0 ? (
        <p>No available rooms for the selected dates.</p>
      ) : (
        <ul>
          {availableRooms.map((room) => (
            <li key={room._id}>
              <div>
                <h3>{room.title}</h3>
                <p>{room.desc}</p>
                <p>Price: ${room.price}</p>
                <p>Max People: {room.maxPeople}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Reserve;
